import { Carousel } from "react-carousel-minimal";

const CarouselSlider = () => {
  const data = [
    {
      image: "images/Taipei.jpg",
    },
    {
      image: "images/Lanterns.jpg",
    },
    {
      image: "images/Phone.jpg",
    },
    {
      image: "images/Love.JPEG",
    },
    {
      image: "images/Bboys.JPG",
    },
    {
      image: "images/Freeze.JPG",
    },
    {
      image: "images/CarlyChase.JPG",
    },
    {
      image: "images/Cashier.JPG",
    },
    {
      image: "images/Cashier2.JPG",
    },
    {
      image: "images/Clouds.jpg",
    },
    {
      image: "images/Dock.jpg",
    },
    {
      image: "images/LoneFisherman.jpg",
    },
    {
      image: "images/SutroBath.JPEG",
    },
    {
      image: "images/SunsetSkater.JPEG",
    },
    {
      image: "images/Skateboard.JPG",
    },
    {
      image: "images/Pupuseria.jpg",
    },
  ];

  const slideNumberStyle = {
    fontSize: "20px",
    fontWeight: "bold",
  };
  return (
    <div className="App">
      <div style={{ textAlign: "center" }}>
        <h2>React Carousel Minimal</h2>
        <p>
          Easy to use, responsive and customizable carousel component for React
          Projects.
        </p>
        <div
          style={{
            padding: "0 20px",
          }}
        >
          <Carousel
            data={data}
            time={2000}
            dynamicHeight={true}
            radius="10px"
            slideNumber={true}
            slideNumberStyle={slideNumberStyle}
            captionPosition="bottom"
            automatic={false}
            dots={true}
            pauseIconColor="white"
            pauseIconSize="40px"
            slideBackgroundColor="darkgrey"
            slideImageFit="cover"
            thumbnails={true}
            thumbnailWidth="100px"
            style={{
              textAlign: "center",
              maxWidth: "850px",
              maxHeight: "500px",
              margin: "40px auto",
            }}
          />
        </div>
      </div>
    </div>
  );
};

export default CarouselSlider;
